import React from 'react';
import { useState } from 'react';

const Contact = () => {
    const [formData, setFormData] = useState({name: '', email: '', subject: '', message: ''});

    const contactData = {
        phone: '+2347037575894',
        email: 'onomesalomi@gmail.com',
        location: '3 Ismaila Bello Street, Koya Estate, Igbolomu, Ikorodu.'
    };

    const [error, setError] = useState(false);
    const [message, setMessage] = useState('');

    const handleSubmit =(event)=> {
        event.preventDefault();
        if (!formData.name) {
            setError(true);
            setMessage(true);
        }
    }
  return (
    <div className='contact row'>
      <div className='col-md-4 mb-4 mb-md-0'>
        <div className='contact-info mb-5'>
            <span className='mdi mdi-phone'></span>
            <div className='details'>
                <h5>Phone</h5>
                <span>{contactData.phone}</span>
            </div>
        </div>
        <div className='contact-info'>
            <span className='mdi mdi-email'></span>
            <div className='details'>
                <h5>Email Address</h5>
                <span>{contactData.email}</span>
            </div>
        </div>
        <div className='contact-info'>
            <span className='mdi mdi-map-marker'></span>
            <div className='details'>
                <h5>Location</h5>
                <span>{contactData.location}</span>
            </div>
        </div>
      </div>
      <div className='col-md-8'>
        <form className='contact-form' onSubmit={handleSubmit}>
            <div className='row'>
                <div className='column col-md-6'>
                    <div className='form-group'>
                        <input type="text" className='form-control' name='name' placeholder='Your name' onChange={handleChange} value={formData.name}/>
                    </div>
                </div>
            </div>
        </form>
      </div>
    </div>
  );
}

export default Contact;
