import React from 'react';
import Portfolio from './Portfolio';
import './portfolios.scss';

const Portfolios = () => {
    const filters = [
        {
            id: 1,
            name: 'All Projects'
        },
        {
            id: 2,
            name: 'Branding'
        },
        {
            id: 3,
            name: 'Creative'
        },
        {
            id: 4,
            name: 'Design'
        },
        {
            id: 5,
            name: 'Art'
        },
    ];
    const portfolioData = [
        {
            id: 1,
            name: 'UI Design',
            catergory: ['design', 'creative'],
            image: require('../../images/portfolio/img_1.jpg'),
            slug: 'ui-design'
        },
        {
            id: 2,
            name: 'Ripped Jeans',
            catergory: ['branding'],
            image: require('../../images/portfolio/img_2.jpg'),
            slug: 'ripped-jeans'
        },
    ]
    const handleChange =(evt)=> {
        console.log(evt.target.value)
    }
  return (
    <div className='portfolios'>
      <ul className='portfolio-filter list-inline'>
        {filters.map((filter) => (
            <li key={filter.id} 
                className={filter.name.toLowerCase() === activeFilter 
                ? 
                'list-inline-item current'
                : 
                'list-inline-item'}
                onClick={handleChange}
            >{filter.name}</li>
        ))}
      </ul>
      <div className='portfolio-filter-wrapper mb-4'>
        <section className='portfolio-filter-mobile' onChange={(evt) => handleChange(evt)}>
            {filters.map((filter) => (
                <option key={filter.id} value={filter.name}>
                    {filter.name}
                </option>
            ))}
        </section>
      </div>
      <div className='portfolio-wrapper row'>

      </div>
    </div>
  );
}

export default Portfolios;
